import React, { useState, useEffect } from 'react';
import api from '../services/api';

interface EmailTemplate {
  id: number;
  name: string;
  category: string;
  subject: string;
  body_html: string;
  variables: string[];
  usage_count: number;
  is_system_template: boolean;
}

interface EmailSignature {
  id: number;
  name: string;
  html_content: string;
  company_logo_url: string;
  company_name: string;
  is_default: boolean;
}

interface EmailAddon {
  id: number;
  name: string;
  type: string;
  content: string;
  auto_include: boolean;
}

interface EmailAutomationProps {
  socket?: WebSocket | null;
  sendMessage?: (message: string) => void;
  isConnected?: boolean;
}

const EmailAutomation: React.FC<EmailAutomationProps> = ({ socket, sendMessage, isConnected }) => {
  const [activeTab, setActiveTab] = useState<'compose' | 'templates' | 'signatures' | 'addons' | 'history'>('compose');
  const [templates, setTemplates] = useState<EmailTemplate[]>([]);
  const [signatures, setSignatures] = useState<EmailSignature[]>([]);
  const [addons, setAddons] = useState<EmailAddon[]>([]);
  const [loading, setLoading] = useState(false);

  // Form states
  const [composeForm, setComposeForm] = useState({
    type: 'single',
    template_id: '',
    recipient_email: '',
    recipient_emails: '',
    subject: '',
    variables: {} as Record<string, string>,
    send_immediately: true,
    scheduled_at: ''
  });

  const [templateForm, setTemplateForm] = useState({
    name: '',
    category: 'general',
    subject: '',
    body_html: '',
    is_system_template: false
  });

  const [signatureForm, setSignatureForm] = useState({
    name: '',
    html_content: '',
    company_logo_url: '',
    company_name: 'NaviKenz',
    is_default: false
  });

  const [addonForm, setAddonForm] = useState({
    name: '',
    type: 'policy',
    content: '',
    auto_include: false
  });

  // ‚úÖ FIXED: Properly define getDefaultTemplates function
  const getDefaultTemplates = () => [
    {
      name: 'Offer Letter',
      category: 'offer_letter',
      subject: 'Job Offer - {{job_title}} Position at {{company_name}}',
      body_html: `
        <h2>Congratulations {{candidate_name}}!</h2>
        <p>We are pleased to offer you the position of <strong>{{job_title}}</strong> at {{company_name}}.</p>
        <h3>Offer Details:</h3>
        <ul>
          <li>Position: {{job_title}}</li>
          <li>Department: {{department}}</li>
          <li>Start Date: {{start_date}}</li>
          <li>Salary: {{salary_amount}}</li>
          <li>Location: {{work_location}}</li>
        </ul>
        <p>Please confirm your acceptance by replying to this email by {{response_deadline}}.</p>
        <p>We look forward to welcoming you to our team!</p>
      `
    },
    {
      name: 'Interview Invitation',
      category: 'interview',
      subject: 'Interview Invitation - {{job_title}} Position',
      body_html: `
        <h2>Interview Invitation</h2>
        <p>Dear {{candidate_name}},</p>
        <p>Thank you for your interest in the <strong>{{job_title}}</strong> position at {{company_name}}.</p>
        <h3>Interview Details:</h3>
        <ul>
          <li>Date: {{interview_date}}</li>
          <li>Time: {{interview_time}}</li>
          <li>Location: {{interview_location}}</li>
          <li>Interviewer: {{interviewer_name}}</li>
        </ul>
        <p>Please confirm your availability by replying to this email.</p>
        <p>Best regards,<br>{{company_name}} Team</p>
      `
    },
    {
      name: 'Rejection Letter',
      category: 'rejection',
      subject: 'Update on Your Application - {{job_title}}',
      body_html: `
        <h2>Application Update</h2>
        <p>Dear {{candidate_name}},</p>
        <p>Thank you for your interest in the {{job_title}} position and for taking the time to interview with us.</p>
        <p>After careful consideration, we have decided to move forward with another candidate whose experience more closely matches our current needs.</p>
        <p>We encourage you to apply for future opportunities that match your background.</p>
        <p>Best wishes for your job search.</p>
        <p>Sincerely,<br>{{company_name}} Recruitment Team</p>
      `
    }
  ];

  // ‚úÖ FIXED: Properly define useDefaultTemplate function
  const useDefaultTemplate = (templateName: string) => {
    const defaultTemplates = getDefaultTemplates();
    const template = defaultTemplates.find(t => t.name === templateName);
    
    if (template) {
      setTemplateForm({
        name: template.name,
        category: template.category,
        subject: template.subject,
        body_html: template.body_html,
        is_system_template: false
      });
    }
  };

  useEffect(() => {
    loadTemplates();
    loadSignatures();
    loadAddons();
  }, []);

  const loadTemplates = async () => {
    try {
      const response = await api.get('/api/v1/emails/templates');
      setTemplates(response.data.templates || []);
    } catch (error) {
      console.error('Error loading templates:', error);
    }
  };

  const loadSignatures = async () => {
    try {
      const response = await api.get('/api/v1/emails/signatures');
      setSignatures(response.data.signatures || []);
    } catch (error) {
      console.error('Error loading signatures:', error);
    }
  };

  const loadAddons = async () => {
    try {
      const response = await api.get('/api/v1/emails/addons');
      setAddons(response.data.addons || []);
    } catch (error) {
      console.error('Error loading addons:', error);
    }
  };

  const handleTemplateSelect = (templateId: string) => {
    const template = templates.find(t => t.id.toString() === templateId);
    if (template) {
      setComposeForm({
        ...composeForm,
        template_id: templateId,
        subject: template.subject,
        variables: template.variables.reduce((acc, variable) => {
          acc[variable] = '';
          return acc;
        }, {} as Record<string, string>)
      });
    }
  };

  const handleSendEmail = async () => {
    if (!composeForm.template_id) {
      alert('Please select a template');
      return;
    }

    if (composeForm.type === 'single' && !composeForm.recipient_email) {
      alert('Please enter recipient email');
      return;
    }

    if (composeForm.type === 'bulk' && !composeForm.recipient_emails) {
      alert('Please enter recipient emails');
      return;
    }

    setLoading(true);
    try {
      let endpoint = '/api/v1/emails/send';
      let payload: any = {
        template_id: composeForm.template_id,
        variables: composeForm.variables,
        send_immediately: composeForm.send_immediately,
        scheduled_at: composeForm.scheduled_at
      };

      if (composeForm.type === 'single') {
        payload.recipient_email = composeForm.recipient_email;
      } else {
        endpoint = '/api/v1/emails/send-bulk';
        payload.name = `Campaign ${new Date().toLocaleString()}`;
        payload.recipient_type = composeForm.type;
        
        if (composeForm.type === 'bulk') {
          payload.recipient_data = composeForm.recipient_emails.split(',').map(email => email.trim());
        }
      }

      const response = await api.post(endpoint, payload);

      if (response.data.success) {
        alert(composeForm.type === 'single' ? 'Email sent successfully!' : `Bulk email sent to ${response.data.sent_count} recipients`);
        
        setComposeForm({
          type: 'single',
          template_id: '',
          recipient_email: '',
          recipient_emails: '',
          subject: '',
          variables: {},
          send_immediately: true,
          scheduled_at: ''
        });

        if (sendMessage && isConnected) {
          sendMessage(`Sent ${composeForm.type} email using template: ${templates.find(t => t.id.toString() === composeForm.template_id)?.name}`);
        }
      }
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Failed to send email');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTemplate = async () => {
    if (!templateForm.name || !templateForm.subject || !templateForm.body_html) {
      alert('Please fill in all required fields');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/api/v1/emails/templates', templateForm);
      
      if (response.data.success) {
        alert('Email template created successfully');
        setTemplateForm({
          name: '',
          category: 'general',
          subject: '',
          body_html: '',
          is_system_template: false
        });
        loadTemplates();
      }
    } catch (error) {
      console.error('Error creating template:', error);
      alert('Failed to create template');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateSignature = async () => {
    if (!signatureForm.name || !signatureForm.html_content) {
      alert('Please fill in required fields');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/api/v1/emails/signatures', signatureForm);
      
      if (response.data.success) {
        alert('Email signature created successfully');
        setSignatureForm({
          name: '',
          html_content: '',
          company_logo_url: '',
          company_name: 'NaviKenz',
          is_default: false
        });
        loadSignatures();
      }
    } catch (error) {
      console.error('Error creating signature:', error);
      alert('Failed to create signature');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateAddon = async () => {
    if (!addonForm.name || !addonForm.content) {
      alert('Please fill in required fields');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/api/v1/emails/addons', addonForm);
      
      if (response.data.success) {
        alert('Email addon created successfully');
        setAddonForm({
          name: '',
          type: 'policy',
          content: '',
          auto_include: false
        });
        loadAddons();
      }
    } catch (error) {
      console.error('Error creating addon:', error);
      alert('Failed to create addon');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="email-automation">
      <div className="page-header">
        <h1>üìß Email Automation</h1>
        <p>Streamline your recruitment communication with smart email templates and bulk sending</p>
      </div>

      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'compose' ? 'active' : ''}`}
          onClick={() => setActiveTab('compose')}
        >
          ‚úâÔ∏è Compose Email
        </button>
        <button 
          className={`tab-button ${activeTab === 'templates' ? 'active' : ''}`}
          onClick={() => setActiveTab('templates')}
        >
          üìù Templates
        </button>
        <button 
          className={`tab-button ${activeTab === 'signatures' ? 'active' : ''}`}
          onClick={() => setActiveTab('signatures')}
        >
          ‚úçÔ∏è Signatures
        </button>
        <button 
          className={`tab-button ${activeTab === 'addons' ? 'active' : ''}`}
          onClick={() => setActiveTab('addons')}
        >
          üîß Add-ons
        </button>
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          üìä History
        </button>
      </div>

      {/* Templates Tab */}
      {activeTab === 'templates' && (
        <div className="tab-content">
          {/* Quick Start Templates */}
          <div className="quick-start-section">
            <h3>üöÄ Quick Start Templates</h3>
            <p>Use these pre-built templates to get started quickly:</p>
            <div className="default-templates-grid">
              {getDefaultTemplates().map((template, index) => (
                <div key={index} className="default-template-card">
                  <h4>{template.name}</h4>
                  <span className="category-badge">{template.category}</span>
                  <p>{template.subject}</p>
                  <button 
                    className="btn-small"
                    onClick={() => useDefaultTemplate(template.name)}
                  >
                    Use Template
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Template Creation Form */}
          <div className="template-creation">
            <h3>üìù Create Custom Template</h3>
            
            <div className="form-grid">
              <div className="form-group">
                <label>Template Name *</label>
                <input
                  type="text"
                  placeholder="e.g., Welcome Email"
                  value={templateForm.name}
                  onChange={(e) => setTemplateForm({...templateForm, name: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>Category</label>
                <select
                  value={templateForm.category}
                  onChange={(e) => setTemplateForm({...templateForm, category: e.target.value})}
                >
                  <option value="general">General</option>
                  <option value="onboarding">Onboarding</option>
                  <option value="offer_letter">Offer Letter</option>
                  <option value="interview">Interview</option>
                  <option value="rejection">Rejection</option>
                  <option value="follow_up">Follow Up</option>
                </select>
              </div>

              <div className="form-group full-width">
                <label>Subject Line *</label>
                <input
                  type="text"
                  placeholder="Welcome to {{company_name}} - {{candidate_name}}"
                  value={templateForm.subject}
                  onChange={(e) => setTemplateForm({...templateForm, subject: e.target.value})}
                />
                <small>Use {`{{variable_name}}`} for dynamic content</small>
              </div>

              <div className="form-group full-width">
                <label>Email Body (HTML) *</label>
                <textarea
                  placeholder="<h2>Welcome {{candidate_name}}!</h2><p>We're excited to have you join {{company_name}}...</p>"
                  value={templateForm.body_html}
                  onChange={(e) => setTemplateForm({...templateForm, body_html: e.target.value})}
                  rows={10}
                />
                <small>HTML formatting supported. Use {`{{variable_name}}`} for personalization</small>
              </div>
            </div>

            <button 
              className="btn-primary"
              onClick={handleCreateTemplate}
              disabled={loading}
            >
              {loading ? 'Creating...' : 'üìù Create Template'}
            </button>
          </div>

          {/* Existing Templates */}
          <div className="existing-templates">
            <h3>üìã Existing Templates</h3>
            <div className="templates-grid">
              {templates.map(template => (
                <div key={template.id} className="template-card">
                  <div className="template-header">
                    <h4>{template.name}</h4>
                    <span className={`category-badge ${template.category}`}>
                      {template.category.replace('_', ' ')}
                    </span>
                  </div>
                  <p className="template-subject">{template.subject}</p>
                  <div className="template-stats">
                    <span>Used {template.usage_count} times</span>
                    {template.is_system_template && <span className="system-badge">System</span>}
                  </div>
                  <div className="template-actions">
                    <button className="btn-small">Edit</button>
                    <button className="btn-small">Preview</button>
                    <button className="btn-small">Duplicate</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Add your other tabs (compose, signatures, addons, history) here */}

      <style jsx>{`
        .email-automation {
          max-width: 1200px;
          margin: 0 auto;
          padding: 20px;
        }

        .page-header {
          text-align: center;
          margin-bottom: 30px;
        }

        .tab-navigation {
          display: flex;
          gap: 10px;
          margin-bottom: 30px;
          border-bottom: 2px solid #e5e7eb;
        }

        .tab-button {
          padding: 12px 24px;
          background: transparent;
          border: none;
          border-bottom: 3px solid transparent;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s;
        }

        .tab-button.active {
          border-bottom-color: #3b82f6;
          color: #3b82f6;
        }

        .tab-content {
          background: white;
          padding: 30px;
          border-radius: 12px;
          box-shadow: 0 4px 6px rgba(0,0,0,0.1);
        }

        .quick-start-section {
          background: #f8fafc;
          padding: 20px;
          border-radius: 8px;
          margin-bottom: 30px;
        }

        .default-templates-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 15px;
          margin-top: 15px;
        }

