        }

        .tab-navigation {
          display: flex;
          gap: 10px;
          margin-bottom: 30px;
          border-bottom: 2px solid #e5e7eb;
        }

        .tab-button {
          padding: 12px 24px;
          background: transparent;
          border: none;
          border-bottom: 3px solid transparent;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s;
        }

        .tab-button.active {
          border-bottom-color: #3b82f6;
          color: #3b82f6;
        }

        .tab-button:hover {
          background: #f8fafc;
        }

        .form-card, .results-card {
